ApplicationContext ac = AppUtil.getApplicationContext();
FormDataDao dao = (FormDataDao) ac.getBean("formDataDao");

public String getEnqId(String NPI){
 WorkflowManager workflowManager = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");
 Connection con = null;
 String returnedId="";
 try {
 DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
 con = ds.getConnection();
 if(!con.isClosed()) {
 PreparedStatement stmtSelectFolders = con.prepareStatement("select pmt.id AS pmtId from app_fd_pmt pmt join app_fd_d_preinscription p on (pmt.c_compagne_enquete=p.c_compagne_enquete) where c_npi=?;");
 stmtSelectFolders.setString(1,NPI);
 ResultSet rs1 = stmtSelectFolders.executeQuery();
 while (rs1.next()) {
 returnedId= rs1.getString("pmtId");
 LogUtil.warn("--------------returnedId---------------------", returnedId);
 }
 return returnedId;
 }
 } catch(Exception e) {
 LogUtil.error("verifying NPI and enquete for reclassement ", e, "error");
 return returnedId;
 } finally {
 try {
 if(con != null) {
 con.close();
 }
 } catch(SQLException e) {/* ignored */}
 }
 return returnedId;
 
 }

public void insertTable(String condition,String tableSource,String tableDestination,String idParent,Boolean parent) {
 AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
 Collection demandsRetrieved = dao.find(tableSource,tableSource,condition,null,null,null,null,null);
 LogUtil.warn(tableSource+" row FOUND "+tableDestination+"-----------------------------------", demandsRetrieved.toString());
 if (demandsRetrieved.isEmpty()) {
 LogUtil.warn("Error: Aucun demands Retrieved. Default admin added", "");
 } else {
 for (Object obj : demandsRetrieved) {
 if (obj instanceof FormRow) {
 FormRow row = (FormRow) obj;
 if(parent){
 row.put("id",idParent); 
 }else{
 if(tableSource.equals("menage")){ 
 UUID uuid = UUID.randomUUID();
 String newIdParentM=uuid.toString();
 String mengId=row.get("id");
 LogUtil.warn("mengId-----------------------------------",mengId);
 row.put("id",newIdParentM);
 row.put("id_enquete",idParent);
 String conditionM = "WHERE id='"+mengId+"'";
 insertTable(conditionM,"table_individus","table_indiv_inter",mengId,false,idParent);
 insertTable(conditionM,"education","education_inter",mengId,false,idParent);
 insertTable(conditionM,"identite_individu","id_individu_inter",mengId,false,idParent);
 insertTable(conditionM,"handicap","handicap_inter",mengId,false,idParent); 
 }else{
 row.put("id",idParent);
 row.put("id_parent",idParent);
 }
 }
 FormRowSet rowSet = new FormRowSet();
 rowSet.add(row);
 dao.saveOrUpdate(tableDestination,tableDestination,rowSet); 
 }
 }
 }
}

