public String getPovertyCategory(int tailleMenage, int score) {
 switch (tailleMenage) {
 case 1:
 if (score >= 0 && score <= 13) {
 return "Extrême pauvreté";
 } else if (score > 13 && score <= 14) {
 return "Pauvreté";
 }
 break;

 case 2:
 if (score >= 0 && score <= 12) {
 return "Extrême pauvreté";
 } else if (score > 12 && score <= 13) {
 return "Pauvreté";
 }
 break;

 case 3: // For 2+ households
 default:
 if (score >= 0 && score <= 12) {
 return "Extrême pauvreté";
 } else if (score > 12 && score <= 13) {
 return "Pauvreté";
 }
 break;
 }
 return "Non pauvre"; // Default if no conditions are met
}


public void getPMT() {
 AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
 String idPMT = "#form.pmt.id#";
 LogUtil.info("id PMT-------------","#form.pmt.id#");
 String condition = "WHERE id='"+idPMT+"'";
 Collection pmt = dao.find("enquete_pmt_id","pmt", condition, null, null, null, null,null);
 LogUtil.warn("pmt FOUD-----------------------------------",pmt.toString());
 if (pmt.isEmpty()) {
 LogUtil.warn("error Aucun pmtne retrieved","");
 } else{
 for (Object obj : pmt) {
 if (obj instanceof FormRow) {
 String num_menage=getMenage(idPMT);
 Random random = new Random();
 int randomNumber = random.nextInt(100) + 1; 
 int score=randomNumber;
 int result = Integer.parseInt(num_menage);
 String category=getPovertyCategory(result,score);
 obj.put("score",score);
 obj.put("categorie",category);
 FormRowSet rowSet = new FormRowSet();
 rowSet.add(obj);
 LogUtil.warn("final obj -----------------------------------",obj.toString());
 try{
 String recordID ="#form.pmt.id#";
 Connection con = null;
 try {
 Connection con = null;
 DataSource ds = (DataSource) AppUtil.getApplicationContext().getBean("setupDataSource");
 con = ds.getConnection();
