 PreparedStatement updateStatement = con.prepareStatement(updateQuery);
 updateStatement.setString(1,score.toString());
 updateStatement.setString(2,category.toString());
 updateStatement.setString(3,recordID);
 updateStatement.executeUpdate();
 } catch (SQLException e) {
 LogUtil.error("Beanshell", e, "SQL Exception occurred during insert.");
 } finally {
 // Close the resources
