 LogUtil.warn("menage FOUD-----------------------------------",pmt.toString());
 if (pmt.isEmpty()) {
 LogUtil.warn("error Aucun pmtne retrieved","");
 return "0";
 } else{
 for (Object obj : pmt) {
 if (obj instanceof FormRow) {
 return obj.get("hh48");
 }
 }
 }
