    UserSecurity us = DirectoryUtil.getUserSecurity();
    UserDao userDao = (UserDao) AppUtil.getApplicationContext().getBean("userDao");
    RoleDao roleDao = (RoleDao) AppUtil.getApplicationContext().getBean("roleDao");
    DepartmentDao departmentDao = (DepartmentDao) AppUtil.getApplicationContext().getBean("departmentDao");
    Department department = departmentDao.getDepartment(deptId);
    Department subDepartment = departmentDao.getDepartment(subDeptId);
    User user = new User();
    user.setId(email);
    user.setUsername(email);
    user.setTimeZone("0");
    user.setActive(1);
    user.setFirstName(firstName);
    user.setLastName(lastName);
    user.setEmail(email);
    
    //Check if there is user security implementation, using it to encrypt password
    if (us != null) {
        user.setPassword(us.encryptPassword(email, password));
    } else {
        user.setPassword(StringUtil.md5Base16(password));
    }
    user.setConfirmPassword(password);
    Set roleSet = new HashSet();
    Role role = roleDao.getRoleByName("User");
    roleSet.add(role);
    user.setRoles(roleSet);
    userDao.addUser(user);
    if (us != null) {
        us.insertUserPostProcessing(user);
    }
    
    if ( department != null ){
        LogUtil.info("departement non nul",department.toString());
        if(deptId.equals("enqueteurs")){
            LogUtil.info("departement =enqueteur",department.toString());
            addToDept(email,deptId,"enqueteur");
        }else if( subDepartment != null ){
            LogUtil.info("sub-departement non nul",subDepartment.toString());
            addToDept(email,subDeptId,gradeId);            
            LogUtil.warn("departement=GUPS-",subDeptId);
        }else{
            LogUtil.warn("departement=",deptId);
            addToDept(email,deptId,gradeId);
