public void addToDept(String userID,String deptId){
    EmploymentDao employmentDao =(EmploymentDao) AppUtil.getApplicationContext().getBean("employmentDao");
    UuidGenerator uuid = UuidGenerator.getInstance();
    String pId = uuid.getUuid();
    Employment emp = new Employment();
    emp.setId(pId);
    emp.setUserId(userID);
    emp.setOrganizationId("rsu");
    emp.setDepartmentId(deptId);
    LogUtil.warn("----- User "+userID+" assigned to departement --->"+deptId,String.valueOf(employmentDao.addEmployment(emp)));
    LogUtil.info("--------------- Affect to departement ---------->", String.valueOf(employmentDao.assignUserToDepartment(userID, deptId)));
}

