public FormRowSet load(Element element, String primaryKey, FormData formData) {
 FormRowSet rows = new FormRowSet();

 // Add default value "--"
 FormRow defaultOption = new FormRow();
 defaultOption.setProperty(FormUtil.PROPERTY_VALUE, "--");
 defaultOption.setProperty(FormUtil.PROPERTY_LABEL, "--");
 rows.add(defaultOption);

 // Array of countries in French
String[] countries = {
 "PANNEAU D’INFORMATION SUR LE MENAGE",
 "LISTE DES MEMBRES DU MENAGE",
 "EDUCATION",
 "HANDICAP",
 "IDENTITE",
 "CARACTERISTIQUES DES MENAGES",
 "EAU ET ASSAINISSEMENT",
 "MOUSTIQUAIRES IMPREGNEES",
 "PULVERISATION INTRA DOMICILIAIRE",
 "CHOCS ET STRATEGIES DE SURVIE",
 "PROTECTION SOCIALE",
 "DEDOUBLONNAGE DES MENAGES",
 "ENQUETE",
 "individus"
};

 // Replace "É" with "E" for countries that start with "É"
 for (int i = 0; i < countries.length; i++) {
 if (countries[i].startsWith("É")) {
 countries[i] = countries[i].replaceFirst("É", "E");
 }
 }

 // Sort countries alphabetically
 Arrays.sort(countries);

 // Add sorted countries to the rows
 for (String country : countries) {
 FormRow option = new FormRow();
 option.setProperty(FormUtil.PROPERTY_VALUE, country);
 option.setProperty(FormUtil.PROPERTY_LABEL, country);
 rows.add(option);
 }

 return rows;
}

