                                if (errorMessages.some(msg => msg.includes(specificError1) || msg.includes(specificError2))) {
                                    // Redirect to a specific URL
                                    window.location.href = '/jw/web/userview/rsu/rsu_userview/_/welcome';
                                }
                            }
                        });
                    } else {
                        console.log('No error messages found');
                    }
                }
            } catch (err) {
                console.error("Error parsing _FORM_ERRORS:", err);
            }
        }

        // Handle confirmation dialog for the submit button
        document.getElementById('submit')?.addEventListener("click", function (e) {
            e.preventDefault();

            Swal.fire({
                title: 'Êtes-vous sûr de vouloir vous désinscrire du registre social unique? Si vous confirmez, vous serez systématiquement déconnecté.',
                icon: 'warning',
