function hideCheckBoxBasedOnStatut() {
    // Get all rows that contain the 'statut_demande' values
    var rows = document.querySelectorAll('tbody tr');
    
    rows.forEach(function(row) {
        // Get the 'statut_demande' value for the current row from the <td> with class "column_statut_demande"
        var statut = row.querySelector('.column_body.column_statut_demande').innerText.trim();
        
        // Get the checkbox element in the current row
        var checkbox = row.querySelector('.select_checkbox input[type="checkbox"]');
        
        // Condition to hide the checkbox if statut_demande is 'En cours de designation'
        console.log('satut',statut);
            if ((statut != 'A réenquêter')&&(statut !='A enquêter')) {
            checkbox.disabled  = 'true';  // Hide the checkbox
        } else {
            checkbox.style.display = '';  // Ensure checkbox is visible if condition is not met
        }
    });
}

