// Add a MutationObserver to detect changes in the table and reapply the logic
function observeTableChanges() {
    var tableBody = document.querySelector('tbody');

    if (tableBody) {
        // Create a MutationObserver instance
        var observer = new MutationObserver(function(mutationsList, observer) {
            // Reapply the checkbox hiding logic when table body changes
            hideCheckBoxBasedOnStatut();
        });

        // Observe the table body for changes
        observer.observe(tableBody, { childList: true, subtree: true });
    }
}

// Start observing changes in the table body
observeTableChanges();
