$(`<script>`).attr("src", `https://cdn.jsdelivr.net/npm/sweetalert2@11`).appendTo('head');
$(document).ready(function(){
    $(`<script>`).text(`
        function dlPostActionDelete(element, message) {
            if (message != "") {
                Swal.fire({
                    title: 'Êtes-vous sûr(e) de vouloir supprimer cette ligne ?',
                    icon: 'question',
                    showCancelButton: true,
                    confirmButtonText: 'Confirmer',
                    cancelButtonText: 'Annuler',
                    confirmButtonColor: "#3085d6",
                    cancelButtonColor: "#d33",
                }).then(({ isConfirmed }) => {
                    if (isConfirmed) {
                        var orgAction = $(element).closest("form").attr("action");
                        $(element).closest("form").removeAttr("target");
                        $(element).closest("form").find("input[type=checkbox]").removeAttr("checked");
                        $(element).closest("form").attr("action", $(element).attr("href"));
                        $(element).closest("form").submit();
                       
                        //reset the action
                        $(element).closest("form").attr("action", orgAction);
                    }
                })
            }
            return false;
        }
    `).appendTo('head')
    
    try{
        let deleteActions=document.querySelectorAll('a .fa.fa-trash');
        deleteActions.forEach((icon)=>{
            let btn=icon.parentElement;
            btn.setAttribute( "onClick", "return dlPostActionDelete(this, 'Confirmez-vous la suppression ?')" );
            console.log(btn);
        });
    }catch(error){
        console.log('cannot set function');
    }
});


